import Card from "../card/card.js"
import React from "react"
import axios from "axios"
import "./trending.css"


const Trending = () => {
    const [nfts, setNfts] = React.useState([])
    const [trendingNfts, setTrendingNfts] = React.useState([])
    const [displayArray, setDisplayArray] = React.useState([])

    React.useEffect(() => {
        axios
            .get(`/api/nfts/list?order=h24_volume_native_asc`)
            .then(response => {
                console.log(response.data)
                setTrendingNfts(response.data)
            })
    }, [])

    React.useEffect(() => {
        const array = trendingNfts.slice(0,4)
        const newArray = array.map((obj,i) => {
            const promise = axios
                    // .get(`${url}${obj.id}`)
                    .get(`/api/nfts/${obj.id}`)
                    .then((response) => {
                        return response.data
                    })
            return promise
        })
        setDisplayArray(newArray)
    },[trendingNfts])

    Promise.all(displayArray).then(response => setNfts(response))

    const trendingCards = 
        nfts.map((obj) => {
            return <Card
                    key={obj.id}
                    title={obj.name}
                    image={obj.image.small}
                    floor_price={obj.floor_price.native_currency}
                />
    })

    return(
        <div className="trending">
                {trendingCards}
                <Card />
                <Card />
                <Card />
                <Card />
        </div>
    )
}

export default Trending;
