import Card from "../card/card.js"
import "./trending.css"

import React from "react"
import axios from "axios"

const Trending = () => {

    const [nftData, setNftData] = React.useState([])
    const [trendingNfts, setTrendingNfts] = React.useState([])
    const [displayArray, setDisplayArray] = React.useState([])
    const url = process.env.REACT_APP_BASE_URL

    React.useEffect(()=> {
        axios
            .get(`${url}`)
            .then(response => {
                setNftData(response.data)
            })
    }, [url])

    React.useEffect(()=> {
        axios
            .get(`${url}/list?order=h24_volume_native_asc`)
            .then(response => {
                setTrendingNfts(response.data)
            })
    }, [url])

    React.useEffect(()=>{
        const updatedArray = trendingNfts.map((obj,i) => {
            let element;
            if (i < 8) {
                const data = nftData.find(x => {
                    return x.id === obj.id
                })
                console.log(data)
                element = <Card key={obj.id} title={obj.name} />
            }
            return element
        })

        setDisplayArray(
            updatedArray
        )
    },[trendingNfts, nftData])

    return(
        <div className="trending">
                {displayArray}
        </div>
    )
}

export default Trending;
